createQuickAdds();
function createQuickAdds(){
    let quickAddsContent = document.querySelector('.quickadd__content');
  quickadds.forEach(item => {
    //Create Item Div
    let qaItem = document.createElement("DIV");
    qaItem.setAttribute('data-item-id', item.id);
    qaItem.classList.add('quickadd__item');
    if(item.sale){
      qaItem.classList.add('sale');
      qaItem.appendChild(createSaleFlag(item));
    }
    
    let header = createItemHeader(item);
    let options = createItemOptions(item);

    qaItem.appendChild(header);
    qaItem.appendChild(options);
    quickAddsContent.appendChild(qaItem);

  });

  function createItemHeader(item){
      //Create Item Header
      let qaItemHeader = document.createElement("div");
      qaItemHeader.classList.add('quickadd__item__label');
      //Create Item Image
      let qaItemImg = document.createElement('img');
      qaItemImg.src = item.img;
      qaItemImg.alt = item.name;
      //Create Item Title
      let qaItemTitle = document.createElement('h3');
      qaItemTitle.textContent = item.name
      //Create Item Price Div
      let qaItemPrice = document.createElement('h4');
      qaItemPrice.classList.add('quickadd__item__price');
      qaItemPrice.textContent = '$' + item.price.toFixed(2);
      //Add Image, Title and Price to header
      qaItemHeader.appendChild(qaItemImg);
      qaItemHeader.appendChild(qaItemTitle);
      qaItemHeader.appendChild(qaItemPrice);

      return qaItemHeader;
  }
  function createItemOptions(item){
    let options = document.createElement('div');
    options.classList.add('quickadd__item__options');
    if(item.styles.length > 0){
      options.appendChild(createStyles(item));
    }
    if(item.sizes.length > 0){
      options.appendChild(createSizes(item));
    }
    if(item.colors.length > 0){
      options.appendChild(createColors(item));
    }
    return options;
  }
  function createSaleFlag(item){
    let saleFlag = document.createElement('div');
    saleFlag.classList.add('sale__flag');
    saleFlag.textContent = item.saleType;
    return saleFlag;
  }
  function createStyles(item){
    let list = document.createElement('ul');
    list.classList.add('styles');
    item.styles.forEach(s => {
      let li = document.createElement('li');
      li.classList.add(item.label + '-' + s.name.substring(0, 4).toLowerCase());
      li.classList.add(item.label);
      li.setAttribute('data-style-id', s.id);
      if(item.styleSpan){
        let span = document.createElement('span');
        span.textContent = s.name;
        li.appendChild(span);
      } else {
        li.textContent = s.name;
      }
      if(s.sizes.length === 0){
        li.classList.add('no-stock');
      }
      list.appendChild(li);
    });
    return list;
  }
  function createSizes(item){
    let list = document.createElement('ul');
    list.classList.add('sizes');
    item.sizes.forEach(s=>{
      let li = document.createElement('li');
      li.classList.add('size');
      li.textContent = s.name;
      li.setAttribute('data-size-id', s.id);
      if(s.styles.length === 0){
        li.classList.add('no-stock');
      }
      list.appendChild(li);
    });
    return list;
  }
  function createColors(item){
    let list = document.createElement('ul');
    list.classList.add('colors');
    item.colors.forEach(c => {
      let li = document.createElement('li');
      li.textContent = c.name;
      li.classList.add(item.label + '-' + c.name.substring(0,4).toLowerCase());
      li.setAttribute('data-color-id', c.id);
      if(!c.stock){
        li.classList.add('no-stock');
      }
      list.appendChild(li);
    })
    return list;
  }
}